package tennis.kata.accelerate

class Player {
    var points: String = "Love"

    fun increaseScore() {
        if (points == "Love") {
            points = "15"
        }
        else if(points == "15") {
            points = "30"
        }
        else if(points == "30"){
            points = "40"
        }
        else if(points == "40"){
            points = "Win"
        }
    }

    val greeting: String
        get() {
            return "Hello World!"
        }
}

fun main() {
    println(Player().greeting)
}
